import {ADD_ARTICLE} from '../constants/action-types'
const forbiddenWords = ["spam", "money"];


function forbiddenWordsMiddleware ({dispatch}) {
    return next => action => {
        if(action.type === ADD_ARTICLE){
            console.log(forbiddenWords.length)
            const foundWord = forbiddenWords.filter(word => {
                console.log(action.payload.title.includes(word), word)
                const include = action.payload.title.includes(word)
                console.log(action.payload.title.includes(word), include)
                return include
            })
            if(foundWord.length){
                return dispatch({type: "FOUND_BAD_WORD"})
            }
            return next(action)
        }
    }
}

export default forbiddenWordsMiddleware
